package io.github.heqyoufree.modifyfgo_alter_lily.imgui

import android.content.Context
import android.graphics.BitmapFactory
import android.graphics.drawable.ColorDrawable
import android.util.Base64
import android.view.View
import android.widget.Button
import android.widget.ImageView
import android.widget.LinearLayout
import android.widget.SeekBar
import android.widget.SeekBar.OnSeekBarChangeListener
import android.widget.Switch
import android.widget.TextView
import android.widget.Toast

class ImGuiView(context: Context?) : LinearLayout(context) {
    private val imgBase64 = ""
    init {
        this.gravity = LinearLayout.HORIZONTAL
        this.minimumWidth = 300
        val linearLayout = LinearLayout(context)
        val but = Switch(context)
        val but2 = Switch(context)
        val textView = TextView(context)
        val seekBar = SeekBar(context)
        val but3 = Button(context)
        val imageView = ImageView(context)

        linearLayout.background = ColorDrawable(0x6f2f2f2f)
        linearLayout.orientation = LinearLayout.VERTICAL
        linearLayout.gravity = LinearLayout.HORIZONTAL

        but.text = "撤退胜利"
        but.isChecked = true
        but.setOnClickListener {
            setBattleWin(but.isChecked)
            Toast.makeText(context, "撤退胜利 ${if (but.isChecked) "on" else "off"}", Toast.LENGTH_SHORT).show()
        }

        but2.text = "120FPS"
        but2.isChecked = true
        but2.setOnClickListener {
            setHighFrameRate(but2.isChecked)
            Toast.makeText(context, "120FPS ${if (but2.isChecked) "on" else "off"}", Toast.LENGTH_SHORT).show()
        }

        textView.text = "全局加速"

        seekBar.max = 400
        seekBar.setOnSeekBarChangeListener(object :OnSeekBarChangeListener {
            override fun onProgressChanged(seekBar: SeekBar?, progress: Int, fromUser: Boolean) { }
            override fun onStartTrackingTouch(seekBar: SeekBar?) { }
            override fun onStopTrackingTouch(seekBar: SeekBar?) {
                if (seekBar != null) {
                    setAccelerate((seekBar.progress.toFloat() + 100) / 100)
                    Toast.makeText(context, "全局加速调整为 ${
                        (seekBar.progress.toFloat() + 100) / 100}", Toast.LENGTH_SHORT).show()
                }
            }
        })

        but3.text = "隐藏"
        but3.setOnClickListener {
            linearLayout.visibility = View.GONE
            imageView.visibility = View.VISIBLE
        }

        linearLayout.addView(but)
        linearLayout.addView(but2)
        linearLayout.addView(textView)
        linearLayout.addView(seekBar)
        linearLayout.addView(but3)

        this.addView(linearLayout)

        val decodedString = Base64.decode(imgBase64, Base64.DEFAULT);
        val decodedByte = BitmapFactory.decodeByteArray(decodedString, 0, decodedString.size);
        imageView.minimumWidth = 100
        imageView.minimumHeight = 100
        imageView.setImageBitmap(decodedByte);
        imageView.visibility = View.GONE
        imageView.setOnClickListener {
            linearLayout.visibility = View.VISIBLE
            imageView.visibility = View.GONE
        }
        imageView.setOnLongClickListener {
            this.visibility = View.GONE
            Toast.makeText(context, "菜单已隐藏，重启游戏以重新打开", Toast.LENGTH_SHORT).show()
            return@setOnLongClickListener true;
        }

        this.addView(imageView)
    }

    external fun setBattleWin(value: Boolean)
    external fun setHighFrameRate(value: Boolean)
    external fun setAccelerate(value: Float)
}